import React from "react";
import { FaEdit, FaTrash } from "react-icons/fa";

const Todo = ({ task, toggleCompleted, handleDelete, updateTodo }) => {
  return (
    <div>
      <li
        onClick={() => toggleCompleted(task.id)}
        className={`${
          task.completed ? "completed()" : ""
        } list-group-item rounded d-flex justify-content-between bg-info mb-3`}
      >
        {task.task}
        <div>
        <span className={task.completed ? "completed" : ""}>{task.task}</span>
          <FaEdit
            color="#02182B"
            className="mx-3"
            onClick={() => updateTodo(task.id)}
          />
          <FaTrash color="#D7263D" onClick={() => handleDelete(task.id)} />
        </div>
      </li>
    </div>
  );
};

export default Todo;
