import React, { useState, useEffect } from "react";
import Todo from "./Todo";
import TodoForm from "./TodoForm";

const TodoList = () => {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState("");
  const [editTodo, setEditTodo] = useState(false);

  useEffect(() => {
    const savedTodo = JSON.parse(localStorage.getItem("todos") || "[]");
    setTodos(savedTodo);
  }, []);

  const addTodo = (newTodo) => {
    if (newTodo !== "") {
      const createdTodos = [...todos, newTodo];
      setTodos(createdTodos);
      localStorage.setItem("todos", JSON.stringify(createdTodos));
    }
    setInput("");
  };
  const handleEdit = (value) => {
   setEditTodo(true)
  };
  const handleDelete = (value) => {
    setTodos((createdTodos) => {
      return createdTodos.filter((todo) => todo !== value);
    });
  };

  return (
    <ul className="todo-list">
      <TodoForm input={input} setInput={setInput} addTodo={addTodo} />
      {todos.map((todo) => (
        <Todo
          key={todo}
          todo={todo}
          handleDelete={handleDelete}
          handleEdit={handleEdit}
        />
      ))}
    </ul>
  );
};

export default TodoList;
