import React, { useState, useEffect } from "react";
import Todo from "./Todo";
import TodoForm from "./TodoForm";

const TodoList = () => {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState("");

  useEffect(() => {
    const savedTodo = JSON.parse(localStorage.getItem("todos") || "[]");
    setTodos(savedTodo);
  }, []);

  const addTodo = (newTodo) => {
    if (newTodo !== "") {
      const createdTodos = [...todos, newTodo];
      setTodos(createdTodos);
      localStorage.setItem("todos", JSON.stringify(createdTodos));
    }
    setInput("");
  };

  const editTodo = (oldTodo, newTodo) => {
    const index = todos.indexOf(oldTodo);
    if (index !== -1) {
      const updatedTodos = [...todos];
      updatedTodos[index] = newTodo;
      setTodos(updatedTodos);
      localStorage.setItem("todos", JSON.stringify(updatedTodos));
    }
  };

  const deleteTodo = (value) => {
    setTodos((createdTodos) => {
      return createdTodos.filter((todo) => todo !== value);
    });

  };



  return (
    <ul className="todo-list">
      <TodoForm input={input} setInput={setInput} addTodo={addTodo} />
      {todos.map((todo) => (
        <Todo
          key={todo}
          todo={todo}
          deleteTodo={deleteTodo}
          editTodo={editTodo}
        />
      ))}
    </ul>
  );
};

export default TodoList;
